import React, { useState } from 'react';
import ProductCard from './ProductCard';

const CatalogSection = ({ products, onDetailsClick }) => {
  const allCategories = ['Todas', ...new Set(products.map(p => p.category))];

  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('Todas');

  const filteredProducts = products.filter(product => {
    const matchesSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                          product.description.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCategory = selectedCategory === 'Todas' || product.category === selectedCategory;
    return matchesSearch && matchesCategory;
  });

  return (
    <section id="catalogo" className="py-16">
      <div className="container mx-auto px-4 sm:px-6 lg:px-8">
        <h2 className="text-4xl font-extrabold text-gray-900 text-center mb-12">
          Cat√°logo <span className="text-indigo-600">Completo</span>
        </h2>

        <div className="flex flex-col sm:flex-row justify-center items-center gap-4 mb-10">
          <input
            type="text"
            placeholder="Buscar polos..."
            className="w-full sm:w-1/2 px-5 py-3 rounded-full border-2 border-gray-300 focus:outline-none focus:border-indigo-500 transition-all duration-300 text-lg" // Color cambiado
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <div className="relative w-full sm:w-1/4">
            <select
              className="block appearance-none w-full bg-white border-2 border-gray-300 text-gray-700 py-3 px-5 pr-8 rounded-full leading-tight focus:outline-none focus:border-indigo-500 transition-all duration-300 text-lg" // Color cambiado
              value={selectedCategory}
              onChange={(e) => setSelectedCategory(e.target.value)}
            >
              {allCategories.map(category => (
                <option key={category} value={category}>{category}</option>
              ))}
            </select>
            <div className="pointer-events-none absolute inset-y-0 right-0 flex items-center px-2 text-gray-700">
              <svg className="fill-current h-4 w-4" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20"><path d="M9.293 12.95l.707.707L15.657 8l-1.414-1.414L10 10.828 5.757 6.586 4.343 8z"/></svg>
            </div>
          </div>
        </div>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-10">
          {filteredProducts.length > 0 ? (
            filteredProducts.map((product) => (
              <ProductCard key={product.id} product={product} onDetailsClick={onDetailsClick} />
            ))
          ) : (
            <p className="text-center text-xl text-gray-600 col-span-full">No se encontraron polos con esos criterios.</p>
          )}
        </div>
      </div>
    </section>
  );
};

export default CatalogSection;
