import React, { useState } from 'react';
import Header from './components/Header';
import ProductCard from './components/ProductCard';
import Footer from './components/Footer';
import CatalogSection from './components/CatalogSection';
import ProductDetailModal from './components/ProductDetailModal'; // Importar el modal

const App = () => {
  const [currentPage, setCurrentPage] = useState('home'); // Estado para controlar la página actual
  const [selectedProduct, setSelectedProduct] = useState(null); // Estado para el producto seleccionado en el modal

  const [products] = useState([
    {
      id: 1,
      name: 'Polo "El Gato Cósmico"',
      description: 'Un diseño que te hará viajar por el universo con tu felino favorito.',
      price: '350.00',
      sizes: ['S', 'M', 'L', 'XL'],
      image: '/images/polo-gato-cosmico.png',
      category: 'Animales',
    },
    {
      id: 2,
      name: 'Polo "Cactus Rebelde"',
      description: 'Para los que pinchan pero con estilo. ¡No te quedes sin el tuyo!',
      price: '320.00',
      sizes: ['S', 'M', 'L'],
      image: 'https://via.placeholder.com/400x400/32CD32/FFFFFF?text=Cactus+Rebelde',
      category: 'Frases divertidas',
    },
    {
      id: 3,
      name: 'Polo "Taco Power"',
      description: 'Demuestra tu amor por la comida mexicana con este diseño épico.',
      price: '380.00',
      sizes: ['M', 'L', 'XL'],
      image: 'https://via.placeholder.com/400x400/FFD700/000000?text=Taco+Power',
      category: 'Cultura pop',
    },
    {
      id: 4,
      name: 'Polo "Ajolote Kawaii"',
      description: 'El anfibio más tierno de México, ahora en tu polo favorito.',
      price: '360.00',
      sizes: ['S', 'M'],
      image: 'https://via.placeholder.com/400x400/8A2BE2/FFFFFF?text=Ajolote+Kawaii',
      category: 'Animales',
    },
    {
      id: 5,
      name: 'Polo "Lucha Libre Mask"',
      description: 'Siente la adrenalina del ring con esta máscara icónica.',
      price: '390.00',
      sizes: ['L', 'XL'],
      image: 'https://via.placeholder.com/400x400/DC143C/FFFFFF?text=Lucha+Mask',
      category: 'Cultura pop',
    },
    {
      id: 6,
      name: 'Polo "Frida Kahlo Pop"',
      description: 'Un tributo moderno a la gran artista mexicana. ¡Arte que se viste!',
      price: '400.00',
      sizes: ['S', 'M', 'L', 'XL'],
      image: 'https://via.placeholder.com/400x400/FF1493/FFFFFF?text=Frida+Pop',
      category: 'Cultura pop',
    },
    {
      id: 7,
      name: 'Polo "Soy tu padre"',
      description: 'Para los fans de la ciencia ficción y los papás con humor.',
      price: '370.00',
      sizes: ['M', 'L', 'XL'],
      image: 'https://via.placeholder.com/400x400/4682B4/FFFFFF?text=Soy+tu+padre',
      category: 'Frases divertidas',
    },
    {
      id: 8,
      name: 'Polo "Perrito Feliz"',
      description: 'La felicidad de un perrito, estampada en tu pecho.',
      price: '340.00',
      sizes: ['S', 'M', 'L'],
      image: 'https://via.placeholder.com/400x400/FFC0CB/000000?text=Perrito+Feliz',
      category: 'Animales',
    },
    {
      id: 9,
      name: 'Polo "Tu idea aquí"',
      description: '¡Haz tu diseño realidad! Envíanos tu idea y la estampamos.',
      price: '450.00',
      sizes: ['S', 'M', 'L', 'XL'],
      image: 'https://via.placeholder.com/400x400/A9A9A9/FFFFFF?text=Personalizado',
      category: 'Personalizados',
    },
    {
      id: 10,
      name: 'Polo "Gamer Mode On"',
      description: 'Para los que viven en el mundo virtual. ¡Game on!',
      price: '360.00',
      sizes: ['M', 'L'],
      image: 'https://via.placeholder.com/400x400/800080/FFFFFF?text=Gamer+Mode',
      category: 'Cultura pop',
    },
    {
      id: 11,
      name: 'Polo "No soy mañanero"',
      description: 'La frase perfecta para los que odian las mañanas.',
      price: '330.00',
      sizes: ['S', 'M', 'L'],
      image: 'https://via.placeholder.com/400x400/FF4500/FFFFFF?text=No+mañanero',
      category: 'Frases divertidas',
    },
    {
      id: 12,
      name: 'Polo "Diseño a tu gusto"',
      description: 'Cuéntanos tu idea y nosotros la hacemos polo.',
      price: '480.00',
      sizes: ['S', 'M', 'L', 'XL'],
      image: 'https://via.placeholder.com/400x400/6A5ACD/FFFFFF?text=Tu+Diseño',
      category: 'Personalizados',
    },
  ]);

  const reviews = [
    {
      name: 'Ana G.',
      comment: '¡Me encantó mi polo del Gato Cósmico! La tela es súper suave y el diseño es único. ¡Ya quiero pedir otro!',
    },
    {
      name: 'Carlos R.',
      comment: 'Pedí un polo personalizado y quedó justo como lo imaginé. La atención por WhatsApp fue rapidísima y muy amable. ¡Recomendados!',
    },
    {
      name: 'Sofía M.',
      comment: 'Los diseños de cultura pop son lo máximo. Compré el de Lucha Libre y todos mis amigos me preguntan dónde lo conseguí. ¡Calidad top!',
    },
  ];

  const whatsappPersonalizarMessage = "Hola, quiero personalizar un polo con mi diseño.";
  const whatsappPersonalizarLink = `https://wa.me/51910940973?text=${encodeURIComponent(whatsappPersonalizarMessage)}`; // Número de WhatsApp actualizado

  const handleDetailsClick = (product) => {
    setSelectedProduct(product);
  };

  const handleCloseModal = () => {
    setSelectedProduct(null);
  };

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col">
      <Header currentPage={currentPage} setCurrentPage={setCurrentPage} />
      <main className="flex-grow">
        {currentPage === 'home' && (
          <>
            {/* Banner Section */}
            <section className="relative bg-gradient-to-r from-purple-500 via-pink-500 to-red-500 text-white py-20 sm:py-24 lg:py-32 flex items-center justify-center text-center">
              <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
                <h2 className="text-5xl sm:text-6xl lg:text-7xl font-extrabold leading-tight mb-8 drop-shadow-lg">
                  Estampa tu estilo con diseños únicos
                </h2>
                <button
                  onClick={() => setCurrentPage('catalog')}
                  className="inline-block bg-white text-purple-600 text-xl sm:text-2xl font-bold px-10 py-4 rounded-full shadow-xl hover:bg-gray-100 transform transition-transform duration-300 hover:scale-105"
                >
                  Ver catálogo
                </button>
              </div>
            </section>

            {/* Featured Products Section - Now showing 3 random products */}
            <section className="py-16 bg-gray-100">
              <div className="container mx-auto px-4 sm:px-6 lg:px-8">
                <h2 className="text-4xl font-extrabold text-gray-900 text-center mb-10">
                  Nuestros <span className="text-indigo-600">Destacados</span> {/* Color cambiado */}
                </h2>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
                  {products.sort(() => 0.5 - Math.random()).slice(0, 3).map((product) => (
                    <ProductCard key={product.id} product={product} onDetailsClick={handleDetailsClick} />
                  ))}
                </div>
              </div>
            </section>

            {/* Personalize Section */}
            <section id="personalizar" className="py-16 bg-gradient-to-r from-green-400 to-blue-500 text-white text-center">
              <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
                <h2 className="text-4xl sm:text-5xl lg:text-6xl font-extrabold leading-tight mb-8 drop-shadow-lg">
                  ¿Quieres tu propio diseño?
                </h2>
                <p className="text-xl sm:text-2xl mb-10">
                  ¡Haz tu idea realidad! Estampa tu estilo único con un polo 100% personalizado.
                </p>
                <a
                  href={whatsappPersonalizarLink}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="inline-block bg-white text-green-600 text-xl sm:text-2xl font-bold px-10 py-4 rounded-full shadow-xl hover:bg-gray-100 transform transition-transform duration-300 hover:scale-105"
                >
                  Personalizar por WhatsApp
                </a>
              </div>
            </section>

            {/* Customer Reviews Section */}
            <section id="opiniones" className="py-16 bg-gray-100">
              <div className="container mx-auto px-4 sm:px-6 lg:px-8">
                <h2 className="text-4xl font-extrabold text-gray-900 text-center mb-12">
                  Lo que dicen nuestros <span className="text-indigo-600">Clientes</span> {/* Color cambiado */}
                </h2>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
                  {reviews.map((review, index) => (
                    <div key={index} className="bg-white p-8 rounded-2xl shadow-lg transform transition-transform duration-300 hover:scale-105 hover:shadow-xl">
                      <p className="text-gray-700 text-lg italic mb-6">"{review.comment}"</p>
                      <p className="text-gray-900 font-bold text-xl">- {review.name}</p>
                    </div>
                  ))}
                </div>
              </div>
            </section>
          </>
        )}

        {currentPage === 'catalog' && (
          <CatalogSection products={products} onDetailsClick={handleDetailsClick} />
        )}
      </main>
      <Footer />
      <ProductDetailModal product={selectedProduct} onClose={handleCloseModal} />
    </div>
  );
};

export default App;

